Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_LEFT",0,-45);
GameTooltip:SetText(GARRISON_LANDING_PAGE_TITLE, 1, 1, 1);
GameTooltip:AddLine(MINIMAP_GARRISON_LANDING_PAGE_TOOLTIP, nil, nil, nil, true);
GameTooltip:Show();

Scripts.OnEvent:
if ( event == "UPDATE_PENDING_MAIL" ) then
if ( HasNewMail() ) then
self:Show();
if( GameTooltip:IsOwned(self) ) then
MinimapMailFrameUpdate();
end
else
self:Hide();
end
end

Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_LEFT",0,-55);
if( GameTooltip:IsOwned(self) ) then
MinimapMailFrameUpdate();
end

Scripts.OnShow:

PlaySound(SOUNDKIT.ACHIEVEMENT_MENU_OPEN     );
self.animationValue = -400
local name = self:GetName()
local start = GetTime()
 addToAnimation(name,self.animationValue,0,start,0.2,function()

    local prog = animations[name]['progress']
    local a = lerp(0,1,(GetTime()-start)/0.2)
    self:SetAlpha(a)
    self:SetPoint('CENTER',0,prog);

end)

Scripts.OnEnter:
if (self.name) then
    GameTooltip:SetOwner(self, "ANCHOR_PRESERVE");
    GameTooltip:ClearAllPoints();
    GameTooltip:SetPoint("TOPLEFT", self.Count, "BOTTOMRIGHT", -20, -20);
    GameTooltip:AddLine(self.name);
    if (self.description) then
        GameTooltip:AddLine(self.description, 1, 1, 1, true);
    end
    GameTooltip:Show();
end
